#turn giant number into 13-digit groups
def split_number(number)
	array = number.to_s.split('')
	grouped_array = []
	group = ""
	count = 0
	step = 0
	add = step

	while (array.length - step) >= 13
		if count < 12
			group << array[add]
			add += 1
			count += 1
		elsif count == 12
			group << array[add]
			grouped_array << group
			group = ""
			count = 0
			step += 1
			add = step
		end
	end
	grouped_array
end

#eliminate any groups containing a zero
def delete_zeroed_groups(array)
	array.reject! {|num| num.include?("0")}
	array
end

#turn number strings into numbers again
def number_string_to_number(array)
	new_array = array.map do |num_string|
		num_string.to_i
	end
	new_array
end

#multiply all digits of a number to return product
def multiply_digits_of_number_for_product(array)
	large_prod = 0
	array.each do |num|
		temp_arr = num.to_s.split('').map(&:to_i)
		j = 1
		product = temp_arr[0]
		while j < temp_arr.length
			product *= temp_arr[j]
			j += 1
		end

		if product > large_prod
			large_prod = product
		end
		product = 1
	end
	puts large_prod
end




multiply_digits_of_number_for_product(number_string_to_number(delete_zeroed_groups(split_number())))